let S="\x20",C=":"+S,F=n=>Math.floor(n),R=n=>Math.random()*n,W=6e2,H=9e2,A=n=>F(R(n-6))+3,x=A(W),y=A(H),X=A(W),Y=A(H),d=((x-X)**2+(y-Y)**2)**.5,r=F(R(d*.9)+d*1.1);console.log(`\u5bf8\u6cd5${C+W}mm${S}x${S+H}mm\n\x20\u70b9A${C}(${(""+x).padStart(3,S)}mm,${S+(""+y).padStart(3,S)}mm)\n\x20\u70b9B${C}(${(""+X).padStart(3,S)}mm,${S+(""+Y).padStart(3,S)}mm)\n\u8ddd\u96e2${C+d.toFixed(2)}mm\n\u7d10\u9577${C+r}mm\n\u500d\u7387${C}\u2252${S}x${(r/d).toFixed(2)}`);
